/*
	Function for the updating of the activeTab class. This class indicates
	to the user which of the available options they are currently on. It
	also defaults to the left-most tab.
*/
var tabs = document.getElementsByClassName("tabButton");

/* 	
	Initilizes the selected tab to the first one for the login. Initializing it like this
	ensures that if the user does not have javascript enabled on their
	machine, it will not appear stuck on one particular tab.
*/

//This is to be called on focus change events on members of tabButton class
function changeActiveTab() {

	for (var j = 0; j < tabs.length; ++j) {
		tabs[j].className = "tabButton";
		if(tabs[j] == this) {
            this.className += " activeTab";
            $(tabs[j]).attr("aria-selected", true);
            $('#loginIdPanel').attr("aria-labelledby", $(tabs[j]).attr('id'));
        } else {
            $(tabs[j]).attr("aria-selected", false);
        }
	}

	if ($('.login').length) {

        //hide error message after a tab click
        if ($("#casMsg").length > 0){
            document.getElementById("casErrorDiv").style.display="none";
        }

        document.getElementById('loginIdLabel').innerHTML = loginTypesAndLabels[this.childNodes[1].innerHTML][0];
        document.getElementById('PINLabel').innerHTML = loginTypesAndLabels[this.childNodes[1].innerHTML][1];
        document.getElementById('compositeAuthenticationSourceType').value = loginTypesAndLabels[this.childNodes[1].innerHTML][2];

        if(loginTypesAndLabels[this.childNodes[1].innerHTML][2]=="HarvardKey") {
            document.getElementById('username').setAttribute('placeholder', 'me@examplemailprovider.com');
        }
        else {
            document.getElementById('username').setAttribute('placeholder', '');
        }
    }
}

function changeTabKeyPressToClick() {
    $(tabs[i]).keydown(function (event) {
        if (event.which == 13) {
            $(this).click()
        }
    });
}

